"use strict";(self.webpackChunkdocs=self.webpackChunkdocs||[]).push([[567],{5226:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"category","label":"Quick Start","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"What is Orbinum Network?","href":"/quick-start/whats_orbinum","docId":"quick-start/whats_orbinum","unlisted":false},{"type":"link","label":"Wallet Setup","href":"/quick-start/wallet_setup","docId":"quick-start/wallet_setup","unlisted":false}],"href":"/"},{"type":"category","label":"Basic Concepts","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Why Blockchain?","href":"/basic-concepts/blockchain","docId":"basic-concepts/blockchain","unlisted":false},{"type":"link","label":"Hybrid Consensus","href":"/basic-concepts/hybrid-consensus","docId":"basic-concepts/hybrid-consensus","unlisted":false},{"type":"link","label":"Distributed Model Sharding Framework","href":"/basic-concepts/distributed-model-sharding-framework","docId":"basic-concepts/distributed-model-sharding-framework","unlisted":false},{"type":"link","label":"Participatory Governance","href":"/basic-concepts/participatory-governance","docId":"basic-concepts/participatory-governance","unlisted":false},{"type":"link","label":"Participants","href":"/basic-concepts/participants","docId":"basic-concepts/participants","unlisted":false},{"type":"link","label":"Token Model","href":"/basic-concepts/token-model","docId":"basic-concepts/token-model","unlisted":false}],"href":"/basic-concepts"},{"type":"category","label":"Building","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Introduction","href":"/building/introduction","docId":"building/introduction","unlisted":false},{"type":"link","label":"Orbinum SDK","href":"/building/sdk","docId":"building/sdk","unlisted":false},{"type":"link","label":"Smart Contracts","href":"/building/smart-contracts","docId":"building/smart-contracts","unlisted":false}],"href":"/building"},{"type":"category","label":"Using Orbinum","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Pay-per-Compute Model","href":"/using-orbinum/pay-per-compute-model","docId":"using-orbinum/pay-per-compute-model","unlisted":false}],"href":"/using-orbinum"}]},"docs":{"basic-concepts/blockchain":{"id":"basic-concepts/blockchain","title":"Why Blockchain?","description":"Orbinum integrates blockchain technology to address the limitations of traditional, centralized AI development and to foster a more open, transparent, and secure AI ecosystem. Traditional AI development often suffers from:","sidebar":"tutorialSidebar"},"basic-concepts/distributed-model-sharding-framework":{"id":"basic-concepts/distributed-model-sharding-framework","title":"Distributed Model Sharding Framework","description":"The Distributed Model Sharding Framework (DMSF) framework enables Orbinum to efficiently manage AI models through on-chain fragmentation, training, storage, validation, and execution. This system ensures decentralization, scalability, and security, preventing any single entity from controlling the AI model lifecycle.","sidebar":"tutorialSidebar"},"basic-concepts/hybrid-consensus":{"id":"basic-concepts/hybrid-consensus","title":"Hybrid Consensus","description":"Orbinum employs a Hybrid Consensus Mechanism, integrating Nominated Proof of Stake (NPoS) and Proof of Contribution (PoC) to ensure network security, scalability, and fair incentivization. This approach balances stake-based security with merit-based rewards, fostering an ecosystem where both validators and contributors play crucial roles in maintaining and advancing the network.","sidebar":"tutorialSidebar"},"basic-concepts/participants":{"id":"basic-concepts/participants","title":"Participants","description":"Orbinum\u2019s decentralized ecosystem relies on a network of participants with distinct technical roles: Validators, who are the core guardians of the network\'s integrity and operational efficiency, operating Substrate-based nodes and playing a key role in AI model operations; Nominators, who enhance network security by supporting trusted validators with their staked tokens; and Contributors, who provide heterogeneous computational resources orchestrated through a distributed model sharding framework to support various AI tasks.","sidebar":"tutorialSidebar"},"basic-concepts/participatory-governance":{"id":"basic-concepts/participatory-governance","title":"Participatory Governance","description":"The DAO will not function initially until the first version of Orbinum is launched. After this launch, Orbinum will be placed under the control of the DAO.","sidebar":"tutorialSidebar"},"basic-concepts/token-model":{"id":"basic-concepts/token-model","title":"Token Model","description":"The Orbinum Network\'s economic engine is powered by its native utility token, ON, which serves as the primary medium for incentivizing participation, facilitating transactions, and enabling governance within the decentralized AI ecosystem. Orbinum employs a hybrid tokenomics model, transitioning from an initial deflationary phase to a controlled inflationary phase to ensure long-term sustainability.","sidebar":"tutorialSidebar"},"building/introduction":{"id":"building/introduction","title":"Introduction","description":"","sidebar":"tutorialSidebar"},"building/sdk":{"id":"building/sdk","title":"Orbinum SDK","description":"","sidebar":"tutorialSidebar"},"building/smart-contracts":{"id":"building/smart-contracts","title":"Smart Contracts","description":"","sidebar":"tutorialSidebar"},"quick-start/wallet_setup":{"id":"quick-start/wallet_setup","title":"Wallet Setup","description":"","sidebar":"tutorialSidebar"},"quick-start/whats_orbinum":{"id":"quick-start/whats_orbinum","title":"What is Orbinum Network?","description":"Orbinum Network is a decentralized ecosystem designed to redefine AI development, deployment, and accessibility by leveraging blockchain technology. By distributing AI training across a network of participating nodes, Orbinum fosters a collaborative, secure, and scalable environment where individuals and organizations can contribute to AI advancements while maintaining full control over their data.","sidebar":"tutorialSidebar"},"using-orbinum/pay-per-compute-model":{"id":"using-orbinum/pay-per-compute-model","title":"Pay-per-Compute Model","description":"The Orbinum Network implements a sophisticated Pay-per-Compute model, representing a fundamental pillar of its decentralized AI ecosystem. This framework addresses the inherent limitations of traditional, centralized computational infrastructure by establishing a distributed marketplace where users can access heterogeneous processing power on demand, paying granularly for the resources consumed. This approach democratizes access to the computationally intensive tasks associated with advanced AI development and deployment.","sidebar":"tutorialSidebar"}}}}')}}]);